create or replace view Tally_display_inverted as SELECT game_id, day, voter, count(*) as total, group_concat(if(unvoted='Yes',concat('[-]',concat(votee,'(',vote_count,')'),'[/-]'),concat(votee,if(nightfall='Yes','*',''),'(',vote_count,')')) order by vote_count separator ', ')as votes_bgg, group_concat(if(unvoted='Yes',concat('<strike>',concat(votee,'(',vote_count,')'),'</strike>'),concat(votee,if(nightfall='Yes','*',''),'(',vote_count,')')) order by vote_count separator ', ')as votes_html from Tally_votes group by game_id, day, voter order by game_id, day, voter asc;
